import React from "react";


class LifeCell extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      alive:     false,
      row:       props.row,
      column:    props.column,
      neighbors: []
    };

    this.handleClick = this.handleClick.bind(this);

  }

  addNeighbor(item)
  {
    const newNeighborList = [...this.state.neighbors, item];
    this.setState({
      neighbors: newNeighborList
    });
  }

  handleClick()
  {
    this.setState({alive: true});
  }

  render() {
    const divStyle = {
      background: (this.state.alive) ? "red" : "white",
      width: 10,
      height: 10,
      border: "1px solid black"
    };
    return (
        <div 
          className="life-cell" 
          id={"row-" + this.state.row + "-column-" + this.state.column} 
          style={divStyle}
          onClick={this.handleClick}
          >
        </div>
      );
  }
}

export default LifeCell;
