import React from "react";
import LifeCell from "./LifeCell";

const NUM_ROWS = 30;
const NUM_COLS = 50;


class GOLGrid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    	childStates: []
    };

    this.generateChildStates = this.generateChildStates.bind(this);
    this.generateChildren = this.generateChildren.bind(this);
    this.changeChildState = this.changeChildState.bind(this);
    this.LifeGrid         = this.LifeGrid.bind(this);

    this.generateChildStates();
  }

  generateChildStates() {
  	for (var i = 0; i < NUM_ROWS; i++) {
  		const row = [];
  		for (var j = 0; j < NUM_COLS; j++) {
  			row[j] = false;
  		}
  		this.state.childStates[i] = row;
  	}
  }

  generateChildren() {
  	let arr = [];
  	for (var i = 0; i < NUM_ROWS; i++) {
  		const row = [];
  		for (var j = 0; j < NUM_COLS; j++) {
  			row[j] = <LifeCell 
  						alive={this.state.childStates[i][j]} 
  						row={i} 
  						column={j} 
  						report={this.changeChildState} 
  					 />
  		}
  		arr.push(row);
  	}
  	return arr;
  }

  changeChildState(row, column) {
  	console.log("clicked in parent at row " + row + ", column " + column);
  	let newStates = [...this.state.childStates];
  	newStates[row][column] = !newStates[row][column];
  	this.setState({
  		childStates: newStates
  	});
  }

  LifeGrid(props) {
  	const grid = this.generateChildren();
  	var columnString = "";
  	for (var i = 0; i < NUM_COLS; i++) columnString += "auto ";
  	const divStyle = { 
  		width: "50%",
  		display: "grid",
  		"grid-template-columns": columnString,
  		padding: "2px",
  		"margin-left": "25%"
  	};
  	return (
  		<div id="MainGrid" style={divStyle} >{grid}</div>
  	);
  }

  render() {
  	return (
  		<this.LifeGrid />
  	);
  }

}

export default GOLGrid;
