import React from "react";
import LifeCell from "./LifeCell";

const NUM_ROWS = 30;
const NUM_COLS = 20;


class GOLGrid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    	children: []
    };

    this.generateChildren = this.generateChildren.bind(this);
    this.addChild         = this.addChild.bind(this);
    this.LifeGrid         = this.LifeGrid.bind(this);

    this.generateChildren();
  }

  generateChildren() {
  	for (var i = 0; i < NUM_ROWS; i++) {
  		const row = [];
  		for (var j = 0; j < NUM_COLS; j++) {
  			row[j] = <LifeCell row={i} column={j} />
  		}
  		this.state.children[i] = row;
  		console.log(row);
  	}
  }

  addChild(item) {
  	const newChildren = [...this.state.children, item];
  	this.setState({ children: newChildren });
  }

  LifeGrid(props) {
  	const grid = this.state.children;
  	const divStyle = { 
  		display: "grid",
  		"grid-template-columns": "auto auto auto"
  	};
  	return (
  		<div id="MainGrid" style={divStyle} >{grid}</div>
  	);
  }

  render() {
  	return (
  		<this.LifeGrid />
  	);
  }

}

export default GOLGrid;
