import React from "react";


class LifeCell extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      alive:     false,
      row:       props.row,
      column:    props.column,
      neighbors: []
    };
  }

  addNeighbor(item)
  {
    const newNeighborList = [...this.state.neighbors, item];
    this.setState({
      neighbors: newNeighborList
    });
  }

  render() {
    const divStyle = {
      background: "red",
      width: 10,
      height: 10,
      border: "1px solid black"
    };
    return (
        <div className="life-cell" id={"row-" + this.state.row + "-column-" + this.state.column} style={divStyle}>
        </div>
      );
  }
}

export default LifeCell;
